import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:riafy_flutter_app/services/home_services.dart';part 'home_bloc_state.dart';class HomeBlocCubit extends Cubit<HomeBlocState> {  HomeBlocCubit() : super(HomeBlocInitial());  ///getHomePageData  getHomePageData() async {    emit(HomeBlocInitial());    try {      var res = await HomeServices().getHomeData();      emit(HomeDataResponse(res));    } catch (e) {      emit(DataError(e));    }  }}